import "./App.css";
import { useState } from "react";
import { v4 as uuidv4 } from 'uuid'
import List from "./Components/Lists/Lists";
import ParticlesBg from "./Components/Particles/ParticlesBg";
function App() {
  const [text, setText] = useState("");
  const [todolist, setTodolist] = useState([]);
  const [particlesConfig, setParticlesConfig] = useState({
    fullScreen: {
      enable: true,
      zIndex: -1,
    },
    particles: {
      number: {
        value: 10,
        density: {
          enable: true,
          value_area: 800,
        },
      },
      color: {
        value: "#fff",
      },
      shape: {
        type: "circle",
        options: {
          sides: 10,
        },
      },
      opacity: {
        value: 0.8,
        random: true,
        anim: {
          enable: false,
          speed: 2,
          opacity_min: 0.1,
          sync: true,
        },
      },
      size: {
        value: 30,
        random: true,
        anim: {
          enable: true,
          speed: 10,
          size_min: 0.1,
          sync: true,
        },
      },
      rotate: {
        value: 0,
        random: true,
        direction: "clockwise",
        animation: {
          enable: true,
          speed: 5,
          sync: false,
        },
      },
      line_linked: {
        enable: false,
        // "enable": true,
        // "distance": 600,
        // "color": "#ffffff",
        // "opacity": 0.4,
        // "width": 2
      },
      move: {
        enable: true,
        speed: 2,
        direction: "none",
        random: false,
        straight: false,
        out_mode: "out",
        attract: {
          enable: true,
          rotateX: 600,
          rotateY: 1200,
        },
      },
    },
    interactivity: {
      events: {
        onhover: {
          enable: true,
          mode: ["bubble", "attract", "grab"], // Attract mode will pull particles towards the cursor on hover
        },
        onclick: {
          enable: true,
          mode: ["repulse", "push"], // Push mode will add new particles on click
        },
        resize: true,
      },
      modes: {
        grab: {
          distance: 400,
          line_linked: {
            opacity: 1,
          },
        },
        bubble: {
          distance: 400,
          size: 40,
          duration: 2,
          opacity: 0.8,
          speed: 3,
        },
        repulse: {
          distance: 200,
          duration: 0.4,
        },
        push: {
          particles_nb: 1, // Number of particles to add on click
        },
        remove: {
          particles_nb: 2,
        },
        attract: {
          distance: 200, // How close the mouse needs to be to attract particles
          duration: 0.7, // Speed of attraction
          factor: 5, // How many particles will be moved by the cursor
          maxSpeed: 50, // The maximum speed for attracting particles
          speed: 1, // The speed of attracting particles
        },
      },
    },
    retina_detect: true,
    background: {
      color: "#111",
      opacity: ".8",
      image: "",
      position: "50% 50%",
      repeat: "no-repeat",
      size: "cover",
    }
  });

  const resetParticles = () => {
    setParticlesConfig((oldConfig) => ({
      ...oldConfig,
      particles: {
        ...oldConfig.particles,
        number: {
          ...oldConfig.particles.number,
          value: 0, // Set the number of particles to zero
        },
      },
    }));
  };

  const addItem = () => {
    const newTodoItem = {
      id: uuidv4(),
      item: text,
      done: false,
    };
    setTodolist([...todolist, newTodoItem]);
    // localStorage.setItem("TodoList", JSON.stringify(todolist))
    setText("");
  }
  const handleToggle = (itemId) => {
    const newTodoList = todolist.map((listitem) => {
      if (listitem.id === itemId) {
        return { ...listitem, done: !listitem.done };
      }
      return listitem;
    })
    setTodolist(newTodoList);
  }

  const handleDelete = (itemId) => {
    const newTodoList = todolist.filter((listItem) => listItem.id !== itemId);
    setTodolist(newTodoList);
  }

  // console.log("todolist:",todolist );
  return (
    <div className="App">
      {/* <ParticlesBg></ParticlesBg> */}
      <ParticlesBg particlesConfig={particlesConfig} />
      <List todolist={todolist} handleToggle={handleToggle} handleDelete={handleDelete} resetParticles={resetParticles} />
      <h1>Todo List</h1>
      <div className="adder">
        <textarea
          type="text"
          placeholder="Add items to your List"
          value={text}
          onChange={(e) => setText(e.target.value)}

        />
        <span onClick={addItem}>+</span>
      </div>
      <List todolist={todolist} handleToggle={handleToggle} handleDelete={handleDelete} />
    </div>
  );
}

export default App;
