function ParticleBg() {
  const [particlesConfig, setParticlesConfig] = useState({
    // ... Your existing particles configuration
  });

  async function loadParticles(main) {
    await loadFull(main);
  }

  const resetParticles = () => {
    setParticlesConfig((oldConfig) => ({
      ...oldConfig,
      particles: {
        ...oldConfig.particles,
        number: {
          ...oldConfig.particles.number,
          value: 0, // Set the number of particles to zero
        },
      },
    }));
  };

  return (

    <>
      <Particles
        id="tsparticles"
        init={loadParticles}
        options={particlesConfig}
      />
      <button
        onClick={resetParticles}
        style={{
          position: "absolute",
          bottom: "20px", // Adjust as needed
          left: "50%",
          transform: "translateX(-50%)",
          zIndex: 9999, // Ensure it's above the particles
        }}
      >
        Reset Particles
      </button>
    </>


  );
}
export default ParticlesBg;





